import { renderIcon } from 'blockies';
import React from 'react';
import Loader from './Loader';
import { createCanvas } from 'canvas';

export default class Tokens extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            wallet: props.match.params.id,
            kaibal: 0,
            tokens: [],
            tokensbal: [],
            icon: ""
        };
    }


    componentDidMount() {
        Promise.all([
            fetch('https://api.kardiainfo.com/tokens').then(res => res.json()),
            fetch(`https://backend.kardiachain.io/api/v1/addresses/${this.state.wallet}/tokens`).then(res => res.json()),
            fetch(`https://backend.kardiachain.io/api/v1/addresses/${this.state.wallet}`).then(res => res.json())
        ]).then(([urlData, url2Data, url3Data]) => {
            document.title = `${this.state.wallet} - Kardia info`;
            const canvas = createCanvas(50, 50);

            var icon = renderIcon(
                canvas
            );
            this.setState({
                isLoaded: true,
                tokens: urlData.tokens,
                tokensbal: url2Data.data.data,
                kaibal: parseFloat(url3Data.data.balance),
                icon:canvas
            });
        },
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
            }
        )
    }

    render() {
        console.log(this.state.kaibal);
        console.log(this.state.tokensbal);
        console.log(this.state.tokens);
        const { error, isLoaded } = this.state;
        if (error) {
            return <div>Error: {error.message}</div>;
        }
        else if (!isLoaded) {
            return <Loader />;
        }
        else {
            return (
                <div>
                    {this.state.icon}
                    know wallet - {this.state.wallet}
                </div>
            )
        }
    }
}
