import React, { Component } from "react";
import getWeb3 from "./components/getWeb3";
import Blockies from 'react-blockies';
import Loader from "./components/Loader";
import tokenABI from "./ABI/token.json"
import gameABI from "./ABI/game.json"


class Game extends Component {
    constructor(props) {
        super(props);
        this.state = {
            gameAddress: null,
            tokenAddress: null,
            web3: null,
            accounts: null,
            InfoBal: null,
            approved: null,
            gameBal: null,
            winnings: 6,
            multiplier: 2,
            wager: 3,
            hi: 5250,
            lo: 4750,
            game: null,
            token: null,
            loading: false,
            disabled: "",
        };
        this.handleBet = this.handleBet.bind(this);
        this.handleMultiplier = this.handleMultiplier.bind(this);
        this.handleMultiplierMaths = this.handleMultiplierMaths.bind(this);
        this.handleHigh = this.handleHigh.bind(this);
        this.handleLow = this.handleLow.bind(this);
    }
    handleBet(event) {
        this.setState({ wager: event.target.value, winnings: (event.target.value * this.state.multiplier).toFixed(4) });
    }
    handleMultiplier(event) {
        var lo = (950000 / (event.target.value * 100)).toFixed(0);
        var hi = 10000 - lo;
        this.setState({ hi: hi, lo: lo, multiplier: event.target.value, winnings: (event.target.value * this.state.wager).toFixed(4) });

    }
    handleMultiplierMaths(event) {
        if (event.target.value < 1.01) {
            this.setState({ multiplier: 1.01, winnings: (1.01 * this.state.wager).toFixed(4), hi: 9406, lo: 594 });
        }
        else if (event.target.value > 4750) {
            this.setState({ multiplier: 4750, winnings: (4750 * this.state.wager).toFixed(4), hi: 9998, lo: 2 });
        }
        else if (event.target.value <= 4750 && event.target.value >= 1.01) {
            this.setState({ multiplier: event.target.value, winnings: (event.target.value * this.state.wager).toFixed(4) });
        }
        else {
            this.setState({ multiplier: 2, winnings: (2 * this.state.wager).toFixed(4), hi: 5250, lo: 4750 });
        }

    }

    handleHigh(event) {
        if (this.state.approved === true) {
            var wager = this.state.web3.utils.toWei(this.state.wager.toString(), 'ether');
            console.log(wager)
            this.state.game.methods.play(1, (Math.round(this.state.multiplier * 100)), wager).send({ from: this.state.accounts[0] }).then(function (tx) {
                console.log("Transaction: ", tx);
            });
        }
        else {
            this.state.token.methods.approve(this.state.gameAddress, "10000000000000000000000000000000000000000000").send({ from: this.state.accounts[0] }).then(function (tx) {
                console.log("Transaction: ", tx);
            });
        }
    }
    handleLow(event) {
        if (this.state.approved === true) {
            var wager = this.state.web3.utils.toWei(this.state.wager.toString(), 'ether');
            console.log(wager)
            this.state.game.methods.play(0, Math.round(this.state.multiplier * 100), wager).send({ from: this.state.accounts[0] }).then(function (tx) {
                console.log("Transaction: ", tx);
            });
        }
        else {
            this.state.token.methods.approve(this.state.gameAddress, "10000000000000000000000000000000000000000000").send({ from: this.state.accounts[0] }).then(function (tx) {
                console.log("Transaction: ", tx);
            });
        }
    }

    async refreshData() {
        try {
            console.log(this.state)
            // Get network provider and web3 instance.
            var web3
            if (!this.state.web3) {
                web3 = await getWeb3();
            }
            else {
                web3 = this.state.web3;
            }
            console.log('buzz')

            // Use web3 to get the user's accounts.
            const accounts = await web3.eth.getAccounts();

            const tokenAddress = "0xc20574d56c2232e416C8c4c09A41F7F4141E35eC";
            const gameAddress = "0x6f0448A9A0e03F539f3D3bF00363979cfD36b79d";

            const token = new web3.eth.Contract(tokenABI, tokenAddress);
            const game = new web3.eth.Contract(gameABI, gameAddress);

            this.setState({ game, token, gameAddress, tokenAddress })

            var info = await getInfo();

            async function getInfo() {
                var ret = {};
                ret.InfoBal = await token.methods.balanceOf(accounts[0]).call();
                ret.gameBal = await token.methods.balanceOf(gameAddress).call();
                ret.allowed = await token.methods.allowance(accounts[0], gameAddress).call();
                ret.stakedBal = await game.methods.currentBalance(accounts[0]).call();
                ret.originalBal = await game.methods.originalBalance(accounts[0]).call();
                ret.originalBal = await game.methods.id().call();

                ret.gameBal = web3.utils.fromWei(ret.gameBal);
                ret.InfoBal = web3.utils.fromWei(ret.InfoBal);
                if (ret.allowed == 0) {
                    ret.allowed = false;
                }
                else {
                    ret.allowed = true;
                }

                return (ret)
            }
            var lo = (950000 / (this.state.multiplier * 100)).toFixed(0);
            var hi = 10000 - lo;

            if (info.allowed === true) {
                this.setState({ web3, accounts, InfoBal: info.InfoBal, approved: info.allowed, gameBal: info.gameBal, hi: hi, lo: lo });
            }
            else {
                this.setState({ web3, accounts, InfoBal: info.InfoBal, approved: info.allowed, gameBal: info.gameBal, hi: "Approve", lo: "Approve" });
            }


        } catch (error) {
            // Catch any errors for any of the above operations.
            console.error(error);
        }
    }

    componentDidMount = async () => {
        this.refreshData()
        this.interval = setInterval(() => this.refreshData(), 1000);
    };


    render() {
        if (!this.state.web3) {
            return <Loader />;
        }
        var wal = this.state.accounts[0].substring(0, 5) + "..." + this.state.accounts[0].slice(this.state.accounts[0].length - 3);
        return (
            <div className="Game">
                <div className="left">
                    <div className="waltab">
                        <div className="walsec">
                            <div className="top">
                                <div className="l">
                                    <Blockies
                                        seed={this.state.accounts[0]}
                                        size={10}
                                        scale={4}
                                        className="identicon m-r-15"
                                    />
                                    {wal}
                                </div>
                                <div className="r p-l-25 p-t-14 p-b-14">
                                    {numberWithCommas(parseFloat(this.state.InfoBal).toFixed(3)) + " INFO"}
