import React, { Component } from "react";
import getWeb3 from "./components/getWeb3";
import Blockies from 'react-blockies';
import Loader from "./components/Loader";
import tokenABI from "./ABI/token.json"
import gameABI from "./ABI/game.json"


class Game extends Component {
    constructor(props) {
        super(props);
        this.state = {
            gameAddress: null,
            tokenAddress: null,
            web3: null,
            accounts: null,
            InfoBal: null,
            approved: null,
            gameBal: null,
            winnings: 6.0000,
            multiplier: 2,
            wager: 3,
            hi: "> 5250",
            lo: "< 4750",
            game: null,
            token: null,
            loading: false,
            disabled: "",
            profitcol: "",
            newStake:null,
        };
        this.handleBet = this.handleBet.bind(this);
        this.handleMultiplier = this.handleMultiplier.bind(this);
        this.handleMultiplierMaths = this.handleMultiplierMaths.bind(this);
        this.handleHigh = this.handleHigh.bind(this);
        this.handleLow = this.handleLow.bind(this);
        this.handleStake = this.handleStake.bind(this);
    }
    handleStake(event){
        this.setState({newStake: event.target.value})
    }
    handleMaxStake(event){
        this.setState({newStake: this.state.InfoBal})
    }
    handleBet(event) {
        this.setState({ wager: event.target.value, winnings: (event.target.value * this.state.multiplier).toFixed(4) });
    }
    handleMultiplier(event) {
        var lo = (950000 / (event.target.value * 100)).toFixed(0);
        var hi = 10000 - lo;

        hi = "> " + hi
        lo = "< " + lo
        this.setState({
            hi: hi,
            lo: lo,
            multiplier: event.target.value,
            winnings: (event.target.value * this.state.wager).toFixed(4)
        });

    }
    handleMultiplierMaths(event) {
        if (event.target.value < 1.01) {
            this.setState({ multiplier: 1.01, winnings: (1.01 * this.state.wager).toFixed(4), hi: "> 594", lo: "< 9406" });
        }
        else if (event.target.value > 4750) {
            this.setState({ multiplier: 4750, winnings: (4750 * this.state.wager).toFixed(4), hi: "> 9998", lo: "< 2" });
        }
        else if (event.target.value <= 4750 && event.target.value >= 1.01) {
            this.setState({ multiplier: event.target.value, winnings: (event.target.value * this.state.wager).toFixed(4) });
        }
        else {
            this.setState({ multiplier: 2, winnings: (2 * this.state.wager).toFixed(4), hi: "> 5250", lo: "< 4750" });
        }

    }

    handleHigh(event) {
        if (this.state.approved === true) {
            var wager = this.state.web3.utils.toWei(this.state.wager.toString(), 'ether');
            console.log(wager)
            this.state.game.methods.play(1, (Math.round(this.state.multiplier * 100)), wager).send({ from: this.state.accounts[0] }).then(function (tx) {
                console.log("Transaction: ", tx);
            });
        }
        else {
            this.state.token.methods.approve(this.state.gameAddress, "10000000000000000000000000000000000000000000").send({ from: this.state.accounts[0] }).then(function (tx) {
                console.log("Transaction: ", tx);
            });
        }
    }
    handleLow(event) {
        if (this.state.approved === true) {
            var wager = this.state.web3.utils.toWei(this.state.wager.toString(), 'ether');
            console.log(wager)
            this.state.game.methods.play(0, Math.round(this.state.multiplier * 100), wager).send({ from: this.state.accounts[0] }).then(function (tx) {
                console.log("Transaction: ", tx);
            });
        }
        else {
            this.state.token.methods.approve(this.state.gameAddress, "10000000000000000000000000000000000000000000").send({ from: this.state.accounts[0] }).then(function (tx) {
                console.log("Transaction: ", tx);
            });
        }
    }

    async refreshData() {
        try {
            console.log(this.state)
            // Get network provider and web3 instance.
            var web3
            if (!this.state.web3) {
                web3 = await getWeb3();
            }
            else {
                web3 = this.state.web3;
            }
            console.log('buzz')

            // Use web3 to get the user's accounts.
            const accounts = await web3.eth.getAccounts();

            const tokenAddress = "0xc20574d56c2232e416C8c4c09A41F7F4141E35eC";
            const gameAddress = "0x6f0448A9A0e03F539f3D3bF00363979cfD36b79d";

            const token = new web3.eth.Contract(tokenABI, tokenAddress);
            const game = new web3.eth.Contract(gameABI, gameAddress);

            this.setState({ game, token, gameAddress, tokenAddress })

            var info = await getInfo();

            async function getInfo() {
                var ret = {};
                ret.InfoBal = await token.methods.balanceOf(accounts[0]).call();
                ret.gameBal = await token.methods.balanceOf(gameAddress).call();
                ret.allowed = await token.methods.allowance(accounts[0], gameAddress).call();
                ret.stakedBal = await game.methods.currentBalance(accounts[0]).call();
                ret.originalBal = await game.methods.originalBalance(accounts[0]).call();
                ret.gameID = await game.methods.id().call();
                ret.wagered = await game.methods.wagered().call();
                ret.totalWon = await game.methods.totalWon().call();

                ret.gameBal = web3.utils.fromWei(ret.gameBal);
                ret.InfoBal = web3.utils.fromWei(ret.InfoBal);
                ret.totalWon = web3.utils.fromWei(ret.totalWon);
                ret.wagered = web3.utils.fromWei(ret.wagered);
                ret.originalBal = web3.utils.fromWei(ret.originalBal);
                ret.stakedBal = web3.utils.fromWei(ret.stakedBal);
                // eslint-disable-next-line
                if (ret.allowed == 0) {
                    ret.allowed = false;
                }
                else {
                    ret.allowed = true;
                }

                return (ret)
            }
            var lo = (950000 / (this.state.multiplier * 100)).toFixed(0);
            var hi = 10000 - lo;

            hi = "> " + hi
            lo = "< " + lo
            var profit = info.stakedBal - info.originalBal;
            var profitcol;

            if (profit > 0) {
                profitcol = "t-green"
            }
            else if (profit === 0) {
                profitcol = "t-grey"
            }
            else {
                profitcol = "t-red"
            }

            if (info.allowed === true) {
                this.setState({ web3, accounts, InfoBal: info.InfoBal, approved: info.allowed, gameBal: info.gameBal, hi: hi, lo: lo, profit: profit, gameID: info.gameID, totalWon: info.totalWon, wagered: info.wagered, stakedBal: info.stakedBal, profitcol: profitcol });
            }
            else {
                this.setState({ web3, accounts, InfoBal: info.InfoBal, approved: info.allowed, gameBal: info.gameBal, hi: "Approve", lo: "Approve", profit: profit, gameID: info.gameID, totalWon: info.totalWon, wagered: info.wagered, stakedBal: info.stakedBal, profitcol: profitcol });
            }


        } catch (error) {
            // Catch any errors for any of the above operations.
            console.error(error);
        }
    }

    componentDidMount = async () => {
        this.refreshData()
        this.interval = setInterval(() => this.refreshData(), 100);
    };


    render() {
        if (!this.state.web3) {
            return <Loader />;
        }
        var wal = this.state.accounts[0].substring(0, 5) + "..." + this.state.accounts[0].slice(this.state.accounts[0].length - 3);
        return (
            <div className="Game">
                <div className="left">
                    <div className="waltab">
                        <div className="walsec">
                            <div className="top">
                                <div className="l">
                                    <Blockies
                                        seed={this.state.accounts[0]}
                                        size={10}
                                        scale={4}
                                        className="identicon m-r-15"
                                    />
                                    {wal}
                                </div>
                                <div className="r p-l-25 p-t-14 p-b-14">
                                    {numberWithCommas(parseFloat(this.state.InfoBal).toFixed(3)) + " INFO"}
                                </div>
                            </div>
                            <div className="top fs-18">
                                Guess where the next random will land and multiply your intitial investment
                            </div>
                            <div className="bottom game bor p-b-30 p-t-30">
                                <div className="ranCont">
                                    <div className="ranNum m-b-30">
                                        <div className="onenum">0</div>
                                        <div className="onenum">0</div>
                                        <div className="onenum">0</div>
                                        <div className="onenum">0</div>
                                    </div>
                                </div>
                                <div className="gameInp pos-r">
                                    <div className="gameInpCont">
                                        <div className="gameInputs m-b-30">
                                            <div className="infoInp">
                                                <img className="ab-l-m m-l-5" src="./img/smalllogo.png" alt=""></img>
                                                <input type="number" placeholder="Bet amount" value={this.state.wager} className="gametxtinp betamount p-l-26" onChange={this.handleBet}></input>
                                            </div>
                                            <div className="mult fs-20 fw-600 t-bl">×</div>
                                            <input className="gametxtinp multiplier txt-c" placeholder="Multip" type="number" onChange={this.handleMultiplier} onBlur={this.handleMultiplierMaths} value={this.state.multiplier}></input>
                                            <div className="eq fs-20 fw-600 t-bl">=</div>
                                            <div className="infoInp">
                                                <img className="ab-l-m m-l-5" src="./img/smalllogo.png" alt=""></img>
                                                <input disabled value={this.state.winnings} className="gametxtinp winnings p-l-26 f-w-600"></input>
                                            </div>
                                        </div>
                                        <div className="gameBtns">
                                            <button onClick={this.handleHigh}>{this.state.lo}</button>
                                            <button onClick={this.handleHigh}>{this.state.hi}</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="right">
                    <div className="waltab">
                        <div className='walsec'>
                            <div className="top ">
                                <span className="fs-18">
                                    Stake INFO and earn a portion of the profits generated by the game
                                </span>
                                <img alt="" className="avimg" src="./img/smalllogo.png"></img>
                            </div>
                            <div className="poolinfo">
                                <div className="farm">
                                    <p className="t-g txt-c fs-16 m-t-30 m-b-20">Info Tokens staked:</p>
                                    <p className="txt-c fs-20 fw-600  m-t-0 m-b-0">{numberWithCommas(parseFloat(this.state.stakedBal).toFixed(3))} INFO</p>
                                    <p className={this.state.profitcol + ` fs-14 txt-c m-t-5`}>Profit: {(this.state.profit).toFixed(3)} INFO</p>
                                    <button className="stakebtn bl m-l-auto m-r-auto m-t-30">Stake</button>
                                    <br />
                                    <button className="stakebtn gr m-l-auto m-r-auto m-b-20">Unstake</button>
                                </div>
                                <div className="stats">
                                    <h4 className="txt-c fs-18 fw-600">Stats</h4>
                                    <p className="t-g txt-c">INFO staked:  <span className="fw-600 t-b">{numberWithCommas(parseFloat(this.state.gameBal).toFixed(3))}</span></p>
                                    <p className="t-g txt-c">Total games played:  <span className="fw-600 t-b">{this.state.gameID}</span></p>
                                    <p className="t-g txt-c">INFO wagered:  <span className="fw-600 t-b">{numberWithCommas(parseFloat(this.state.wagered).toFixed(3))}</span></p>
                                    <p className="t-g txt-c">INFO won:  <span className="fw-600 t-b">{numberWithCommas(parseFloat(this.state.totalWon).toFixed(3))}</span></p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="modal stake">
                    <div class="topbar m-b-15">
                        <div class="icon-btn ab-l-m m-l-10 ripple hamb-menu cross noselect">
                            <svg class="hamburger-svg opened noselect" width="30" height="30" viewBox="0 0 100 100">
                                <path class="hamburger-line hamburger-line1" d="M 20,29.000046 H 80.000231 C 80.000231,29.000046 94.498839,28.817352 94.532987,66.711331 94.543142,77.980673 90.966081,81.670246 85.259173,81.668997 79.552261,81.667751 75.000211,74.999942 75.000211,74.999942 L 25.000021,25.000058" />
                                <path class="hamburger-line hamburger-line2" d="M 20,50 H 80" />
                                <path class="hamburger-line hamburger-line3" d="M 20,70.999954 H 80.000231 C 80.000231,70.999954 94.498839,71.182648 94.532987,33.288669 94.543142,22.019327 90.966081,18.329754 85.259173,18.331003 79.552261,18.332249 75.000211,25.000058 75.000211,25.000058 L 25.000021,74.999942" />
                            </svg>
                        </div>
                        <div class="title ab-l-m fs-22 t-g m-l-60 f-rob noselect">
                            Stake INFO
                        </div>
                    </div>
                    <div className="cont p-l-15 p-r-15">
                        <p className="txt-r fs-14 t-bl c-pointer" onClick={this.handleMaxStake}>Balance: {numberWithCommas(parseFloat(this.state.InfoBal).toFixed(2))} INFO</p>
                        <div className="infoInp stakeInp">
                            <img className="ab-l-m m-l-5" src="./img/smalllogo.png" alt=""></img>
                            <input type="number" placeholder="Stake amount" value={this.state.newStake} className="gametxtinp stakeInp p-l-26" onChange={this.handleStake}></input>
                        </div>
                    </div>
                </div>
                <div className="modal-overlay"></div>
            </div>
        );
    }
}


function numberWithCommas(x) {
    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}

export default Game;
