import React from 'react'

import { Line, Chart } from 'react-chartjs-2';
import 'chartjs-adapter-date-fns';

export default function Smallchart(props) {
    return (
        <Line
            data={{
                labels: range(props.now, props.time, props.histData.length - 1),
                datasets: [{
                    backgroundColor: props.col,
                    borderColor: props.col,
                    data: props.histData,
                    fill: props.col,
                    lineTension: 0,
                    pointRadius: 0,
                    borderWidth: 3
                }
                ],
            }}
            options={{
                scales: {
                    x: {
                        grid: {
                            display: false,
                            drawBorder: false,
                            offset: true,
                        },
                        ticks: {
                            maxTicksLimit: 6,
                            autoSkip: true,
                            maxRotation: 0,
                            minRotation: 0,
                        },
                        type: "time",
                        gridLines: {
                            color: '#f0f0f0',
                            lineWidth: 1
                        }
                    },
                    y:
                    {
                        grid: {
                            drawBorder: false,
                            precision: 1
                        },
                        ticks: {
                            callback: function (value, index, values) {
                                return ("$" + value.toPrecision(3))
                            },
                            maxTicksLimit: 6,
                            padding: 10
                        }
                    }
                },
                plugins: {
                    legend: {
                        display: false
                    },
                    tooltip: {
                        mode: 'index',
                        intersect: false
                    }
                }
            }}

        />
    )
}

function range(now, time, length) {
    var arr = [now * 1000]
    var timearr = []
    for (let i = 0; i < length; i++) {
        var n = arr[0] - (time * 1000)
        arr.unshift(round(n, (time * 1000)))
    }
    for (let i = 0; i < arr.length; i++) {

        timearr.push(new Date(arr[i]))
    }
    return (timearr)
}

function round(num, time) {
    return Math.ceil(num / time) * time;
}
