import React from 'react'
import Loader from './Loader';
import Calculator from './subcomponents/Calculator';
import Tokenlinks from './subcomponents/Tokenlinks';
import Tokenpairs from './subcomponents/Tokenpairs';
import Bigchart from './subcomponents/Bigchart';

export default class Tokens extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            link: props.match.params.id,
            symbol: props.match.params.id.replace(/_/g, ' '),
            data: [],
            inp1: "",
            inp2: "",
            allHistData: {},
            chartData: [],
            chartCol: "",
            timeframe: 3600,
            now: parseInt(Math.floor(new Date().getTime() / 1000.0))
        };
    }

    componentDidMount() {
        Promise.all([
            fetch('https://kardia-info-backend.herokuapp.com/api').then(res => res.json()),
            fetch(`https://kardia-info-backend.herokuapp.com/api/hist/${this.state.link}`).then(res => res.json())
        ]).then(([urlData, url2Data]) => {
            var d = []
            d.push([...url2Data.weekHist].reverse())
            d = d[0]
            var col = getCol(d)
            this.setState({
                isLoaded: true,
                data: urlData,
                allHistData: url2Data,
                chartData: d,
                chartCol: col
            });
        },
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
            }
        )
    }
    componentWillMount() {
        Chart.pluginService.register({
          afterDraw: function (chart, easing) {
            if (chart.tooltip._active && chart.tooltip._active.length) {
              const activePoint = chart.controller.tooltip._active[0];
              const ctx = chart.ctx;
              const x = activePoint.tooltipPosition().x;
              const topY = chart.scales['y-axis-1'].top;
              const bottomY = chart.scales['y-axis-1'].bottom;
              ctx.save();
              ctx.beginPath();
              ctx.moveTo(x, topY);
              ctx.lineTo(x, bottomY);
              ctx.lineWidth = 2;
              ctx.strokeStyle = '#e23fa9';
              ctx.stroke();
              ctx.restore();
            }
          }
        });
      }


    dayChart = () => {
        var d = []
        d.push([...this.state.allHistData.dayHist].reverse())
        d = d[0]
        var col = getCol(d)
        this.setState({
            chartData: d,
            chartCol: col,
            timeframe: 900
        })
    }
    weekChart = () => {
        var d = []
        d.push([...this.state.allHistData.weekHist].reverse())
        d = d[0]
        var col = getCol(d)
        this.setState({
            chartData: d,
            chartCol: col,
            timeframe: 3600
        })
    }
    monthChart = () => {
        var d = []
        d.push([...this.state.allHistData.monthHist].reverse())
        d = d[0]
        var col = getCol(d)
        this.setState({
            chartData: d,
            chartCol: col,
            timeframe: 14400
        })
    }
    sixMonthChart = () => {
        var d = []
        d.push([...this.state.allHistData.sixMonthHist].reverse())
        d = d[0]
        var col = getCol(d)
        this.setState({
            chartData: d,
            chartCol: col,
            timeframe: 86400
        })
    }
    yearChart = () => {
        var d = []
        d.push([...this.state.allHistData.yearHist].reverse())
        d = d[0]
        var col = getCol(d)
        this.setState({
            chartData: d,
            chartCol: col,
            timeframe: 86400
        })
    }
    allChart = () => {
        var d = []
