import React from 'react'

export default class Overview extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        itemsone: [],
        itemstwo: []
      };
    }

    componentDidMount() {
      fetch("https://backend.kardiachain.io/api/v1/contracts?page=1&limit=50&type=KRC20&status=Verified")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: false,
              itemsone: result
            });
          },
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
      fetch("https://api.info.kaidex.io/api/tokens")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              itemstwo: result
            });
          },
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    render() {
      const { error, isLoaded, items } = this.state;
      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
        var tokens = {
            names: [],
            logos: []
        }

        for (let i = 0; i<items.data.data.length; i++){
            tokens.names[i] = items.data.data[i].name
            tokens.logos[i] = items.data.data[i].logo
        }

        let tokenList=[];
        for(let i = 0; i<tokens.names.length; i++){
            tokenList.push(<div key={i}>Name: {tokens.names[i]} Logo: <img className="tokenlogo" src={tokens.logos[i]}/></div>)
        }


        return (
            <div>
                {tokenList}
            </div>
        )
      }
    }
  }
