import Blockies from 'react-blockies';
import React from 'react';
import Loader from './Loader';
import { Link } from 'react-router-dom';

export default class Tokens extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            wallet: props.match.params.id,
            kaibal: 0,
            tokens: [],
            tokensbal: [],
            icon: ""
        };
    }


    componentDidMount() {
        Promise.all([
            fetch('https://api.kardiainfo.com/tokens').then(res => res.json()),
            fetch(`https://backend.kardiachain.io/api/v1/addresses/${this.state.wallet}/tokens`).then(res => res.json()),
            fetch(`https://backend.kardiachain.io/api/v1/addresses/${this.state.wallet}`).then(res => res.json())
        ]).then(([urlData, url2Data, url3Data]) => {
            document.title = `${this.state.wallet} - Kardia info`;

            this.setState({
                isLoaded: true,
                tokens: urlData.tokens,
                tokensbal: url2Data.data.data,
                kaibal: parseFloat(url3Data.data.balance),
            });
        },
            (error) => {
                this.setState({
                    isLoaded: true,
                    error
                });
            }
        )
    }

    render() {
        const { error, isLoaded } = this.state;
        if (error) {
            return <div>Error: {error.message}</div>;
        }
        else if (!isLoaded) {
            return <Loader />;
        }
        else {
            if(this.state.tokensbal === null && this.state.kaibal === 0){
                return(
                    <div className="p-t-20 p-l-20 p-r-20">
                        This seems to be an invalid wallet
                        <br/>
                        <br/>
                        <Link className="t-bl t-d-none" to="/wallet">Click here to try again</Link>
                    </div>
                )
            }
            else{
                var assets=[]
                for(let i=0; i<this.tokens.length; i++){
                    if(this.tokens[i].id==="0xAF984E23EAA3E7967F3C5E007fbe397D8566D23d"){
                        var obj={
                            name:"KardiaChain",
                            logo:"https://kardiachain-explorer.s3-ap-southeast-1.amazonaws.com/explorer.kardiachain.io/logo/AF984E23EAA3E7967F3C5E007fbe397D8566D23d.png",
                            symbol:"KAI",
                            balance:this.state.kaibal/10**18,
                            usd:obj.balance*this.state.tokens[i].price
                        }
                        assets.push(obj)
                        break;
                    }
                }
                for(let i=0; i<this.state.tokensbal.length;i++){
                    for(let b=0; b<this.state.tokens.length; b++){
                        if(this.state.tokensbal[i].contractAddress.toLowerCase()===this.state.tokens[i].id.toLowerCase()){
                            obj={
                                name:this.state.tokens[b].name,
                                logo:this.state.tokens[b].logo,
                                symbol:this.state.tokensbal[i].tokenSymbol,
                                balance:this.state.tokensbal[i].balance/10**this.state.tokensbal[i].tokenDecimals,
                                usd:obj.balance*this.state.tokens[b].price
                            }
                            assets.push(obj)
                        }
                    }
                }
                console.log(assets)
                return (
                    <div>
                        <Blockies
                            seed={this.state.wallet}
                            size={13} 
                            scale={4}
                            className="identicon" 
                        />
                        Wallet - {this.state.wallet}
                        {assets}
                    </div>
                )
            }
        }
    }
}
