import React from 'react'

export default class Overview extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: []
      };
    }

    componentDidMount() {
      fetch("https://backend.kardiachain.io/api/v1/contracts?page=1&limit=50&type=KRC20&status=Verified")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result
            });
          },
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    render() {
      const { error, isLoaded, items } = this.state;
      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
        var tokens = {
            names: [],
            logos: []
        }

        for (let i = 0; i<items.data.data.length; i++){
            tokens.names[i] = items.data.data[i].name
            tokens.logos[i] = items.data.data[i].logo
        }

        let tokenList=[];
        for(let i = 0; i<tokens.names; i++){
            tokenList.push(<div>Name: {tokens.names[i]} Logo: <img src={tokens.logos[i]}/></div>)
        }


        return (
            <ul>
                {tokens.logos.map((item,index)=>{
                    return(
                        <div>
                            <li key={index}><img src={item}></img></li>
                            <li key={index}><img src={item}></img></li>
                        </div>
                    ) 
                })}

            </ul>
        )
      }
    }
  }
