    handleRewards(event) {
        console.log("hi")
        if (event.target.value > this.state.gameBal / 10.05) {
            this.setState({ wager: (this.state.gameBal / 10.05 / this.state.multiplier).toFixed(4), winnings: (this.state.gameBal / 10.05).toFixed(4) })
        }
    }
    handleBet(event) {
        this.setState({ wager: event.target.value, winnings: (event.target.value * this.state.multiplier).toFixed(4) });
    }
    handleBetMaths(event) {
        if (event.target.value < 0.0001) {
            this.setState({ wager: 0.0001, winnings: (0.0001 * this.state.multiplier).toFixed(4) });
        }
        else if (event.target.value > this.state.InfoBal) {
            this.setState({ wager: this.state.InfoBal, winnings: (this.state.InfoBal * this.state.multiplier).toFixed(4) });
        }
        else {
            this.setState({ wager: event.target.value, winnings: (event.target.value * this.state.multiplier).toFixed(4) });
        }
    }
    handleMultiplier(event) {
        var lo = (950000 / (event.target.value * 100)).toFixed(0);
        var hi = 10000 - lo;

        hi = "> " + hi
        lo = "< " + lo
        this.setState({
            hi: hi,
            lo: lo,
            multiplier: event.target.value,
            winnings: (event.target.value * this.state.wager).toFixed(4)
        });

    }
    handleMultiplierMaths(event) {
        if (event.target.value < 1.01) {
            this.setState({ multiplier: 1.01, winnings: (1.01 * this.state.wager).toFixed(4), hi: "> 594", lo: "< 9406" });
        }
        else if (event.target.value > 4750) {
            this.setState({ multiplier: 4750, winnings: (4750 * this.state.wager).toFixed(4), hi: "> 9998", lo: "< 2" });
        }
        else if (event.target.value <= 4750 && event.target.value >= 1.01) {
            this.setState({ multiplier: event.target.value, winnings: (event.target.value * this.state.wager).toFixed(4) });
        }
        else {
            this.setState({ multiplier: 2, winnings: (2 * this.state.wager).toFixed(4), hi: "> 5250", lo: "< 4750" });
        }
    }

    async handleHigh(event) {
        if (this.state.approved === true) {
            var wager = this.state.web3.utils.toWei(this.state.wager.toString(), 'ether');
            this.setState({ tr: "hi" })
            await this.state.game.methods.play(1, (Math.round(this.state.multiplier * 100)), wager).send({ from: this.state.accounts[0] }).then(
                function (tx) {
                    console.log("Transaction: ", tx);
                }
            );
            this.setState({ tr: "" })
        }
        else {
            this.setState({ tr: "appr" })
            await this.state.token.methods.approve(this.state.gameAddress, "10000000000000000000000000000000000000000000").send({ from: this.state.accounts[0] }).then(
                function (tx) {
                    console.log("Transaction: ", tx);
                }
