import React from 'react'

import { Line } from 'react-chartjs-2';
import 'chartjs-adapter-date-fns';

export default function Smallchart(props) {

    return (
        <Line
            data={{
                labels: range(props.now, props.time, props.histData.length - 1),
                datasets: [{
                    backgroundColor: props.col,
                    borderColor: props.col,
                    data: props.histData,
                    fill: false,
                    lineTension: 0,
                    pointRadius: 0,
                    borderWidth: 3
                }
                ],
            }}
            options={{
                scales: {
                    x: {
                        grid: {
                            display: false,
                            drawBorder: false
                        },
                        ticks: {
                            maxTicksLimit: 8,
                            maxRotation: 0,
                            minRotation: 0,
                        },
                        type: "time"
                    },
                    y:
                    {
                        grid: {
                            drawBorder: false,
                            precision: 1
                        },
                        ticks: {
                            callback: function(value, index, values) {
                                return ("$"+value.toPrecision(2))
                            }
                        }
                    }
                },
                plugins: {
                    legend: {
                        display: false
                    }
                },
                tooltips: {
                    mode: 'nearest',
                    intersect: false
                 },
                 hover: {
                    mode: 'nearest',
                    intersect: false
                 }
            }}

        />
    )
}

function range(now, time, length) {
    var arr = [now * 1000]
    var timearr = []
    for (let i = 0; i < length; i++) {
        arr.unshift(arr[0] - (time * 1000))
    }
    for (let i = 0; i < arr.length; i++) {
        timearr.push(new Date(arr[i]))
    }
    return (timearr)
}
